DROP TABLE IF EXISTS  usuario     CASCADE;
DROP TABLE IF EXISTS  amigo       CASCADE;
DROP TABLE IF EXISTS  publicacao  CASCADE;
DROP TABLE IF EXISTS  comentario  CASCADE;
DROP TABLE IF EXISTS  curtida     CASCADE;
DROP TABLE IF EXISTS  permissao   CASCADE;

CREATE TABLE usuario (
    id               BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    nome_completo    VARCHAR(255)        NOT NULL,
    email            VARCHAR(255)        UNIQUE NOT NULL,
    apelido          VARCHAR(50)         UNIQUE NOT NULL,
    data_nascimento  DATE                NOT NULL,
    senha            VARCHAR(128)        NOT NULL,
    imagem_perfil    VARCHAR(512),
    data_criacao     TIMESTAMP,
    ativo            BOOLEAN             DEFAULT true
);

CREATE TABLE amigo (
    id                 BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    confirmado         BOOLEAN             NOT NULL DEFAULT FALSE,

    usuario_enviando   BIGINT            NOT NULL REFERENCES usuario (id),
    usuario_recebendo  BIGINT			       NOT NULL REFERENCES usuario (id),

    CONSTRAINT uc_amigo UNIQUE (usuario_enviando, usuario_recebendo),
    CHECK (usuario_enviando <> usuario_recebendo)
);

CREATE TABLE publicacao (
    id               BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    data_criacao     TIMESTAMP,
    titulo           VARCHAR(128)        NOT NULL,
    texto            VARCHAR(2048)       NOT NULL,
    imagem           VARCHAR(512),
    visibilidade     VARCHAR(50)         NOT NULL,

    id_usuario       BIGINT              NOT NULL REFERENCES usuario(id)
);

CREATE TABLE comentario (
    id              BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    data_criacao    TIMESTAMP,
    texto           VARCHAR(1024)       NOT NULL,

    id_publicacao   BIGINT              NOT NULL REFERENCES publicacao(id),
    id_usuario      BIGINT              NOT NULL REFERENCES usuario(id)
);

CREATE TABLE curtida (
    id              BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    id_publicacao   BIGINT              NOT NULL REFERENCES publicacao(id),
    id_usuario      BIGINT              NOT NULL REFERENCES usuario(id),

    CONSTRAINT uc_curtida UNIQUE (id_publicacao, id_usuario)
);

CREATE TABLE permissao (
    id              BIGINT PRIMARY KEY  GENERATED BY DEFAULT AS IDENTITY,
    funcao          VARCHAR(100)        NOT NULL,

    usuario_id      BIGINT              NOT NULL REFERENCES usuario(id),

    UNIQUE (funcao, usuario_id)
);

